(function(){tinymce.create('tinymce.plugins.CodeElementPlugin',{init:function(ed,url){ed.addCommand('mceCodeElement',function(){ed.execCommand('mceBeginUndoLevel');var e=ed.dom.getParent(ed.selection.getNode(),'CODE');if(e==null){if(ed.selection.isCollapsed()){ed.execCommand('mceInsertContent',false," <code>code</code>&nbsp;")}else{ed.execCommand('mceReplaceContent',false,' <code>{$selection}</code>')}}else{ed.execCommand('mceRemoveNode',false,e);ed.nodeChanged()}ed.execCommand('mceEndUndoLevel')});ed.addButton('codeElement',{title:'Insert/edit code',cmd:'mceCodeElement',image:url+'/img/code.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('codeElement',n.nodeName=='CODE')})},getInfo:function(){return{longname:'Code Element Plugin',author:'Mauro Bieg',authorurl:'',infourl:'',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('codeElement',tinymce.plugins.CodeElementPlugin)})();